mapper.insert:
INSERT INTO BOARD
(
    TNO
    ,MNO
    ,BTYPE
    ,BTITLE
    ,BCONTENT
    ,BREG_DATE
)
VALUES
(
  	#{tno}
  	,#{mno}
    ,'G'
    ,#{btitle}
    ,#{bcontent}
    ,NOW()
)

	SELECT LAST_INSERT_ID()

mapper.update:
UPDATE BOARD
SET BTITLE = #{btitle}
	,BCONTENT = #{bcontent}
	,BMOD_DATE = NOW()
WHERE BTYPE = 'G'
AND TNO = #{tno}
AND MNO = #{mno}
AND BNO = #{bno}

mapper.delete:
DELETE FROM FILEATTACH
WHERE FNO = #{fno}
AND BNO = #{bno}

mapper.update:
UPDATE FILEATTACH
SET FORI_NAME = #{foriName}
	,FREAL_NAME = #{frealName}
	,FPATH = #{fpath}
	,FTHUMB = #{fthumb}
WHERE FNO = #{fno}
AND BNO = #{bno}
