mapper.update:
update member set mapply_tno = #{mapplyTno}, menter_yn = 'Y', mapply_yn = 'N'
,tno = #{tno} ,menter_yn = 'N'
where mno = #{mno}

update team
	set tmem_cnt = tmem_cnt + 1
 where tno = #{tno}

update team
set tmem_cnt = tmem_cnt - 1
where tno = #{tno}

update member
	  set mposname = #{mposname}
 where mno = #{mno}

update member
	  set mgoal_cnt = #{mgoalCnt},
	      msupport_cnt = #{msupportCnt},
	      mloss_cnt = #{mlossCnt}
 where mno = #{mno}

update team
	  set tlatitude = #{tlatitude},
	      tlongitude = #{tlongitude},
	      tpoint = #{tpoint}
 where tno = #{tno}
